data.SUPABASE_SCHEMA.sql:
-- SlideMint — Supabase Schema (idempotent)
-- Paste into Supabase > SQL editor, then RUN.

-- ============= TABLES =============
create table if not exists public.profiles (
  user_id uuid primary key references auth.users(id) on delete cascade,
  email text not null,
  display_name text,
  affiliate boolean default false,
  created_at timestamptz default now()
);

create table if not exists public.user_credits (
  user_id uuid primary key references auth.users(id) on delete cascade,
  credits integer not null default 0,
  updated_at timestamptz default now()
);

create table if not exists public.credit_ledger (
  id bigserial primary key,
  user_id uuid not null references auth.users(id) on delete cascade,
  delta integer not null,
  reason text not null,
  ref text,
  created_at timestamptz default now()
);

create table if not exists public.slideshows (
  id bigserial primary key,
  user_id uuid not null references auth.users(id) on delete cascade,
  item_id text,
  video_url text not null,
  cover_image text,
  meta jsonb,
  created_at timestamptz default now()
);

-- ============= RLS =============
alter table public.profiles enable row level security;
alter table public.user_credits enable row level security;
alter table public.credit_ledger enable row level security;
alter table public.slideshows enable row level security;

-- Drop old policies if they exist (so re-runs are clean)
drop policy if exists "Own profile read"   on public.profiles;
drop policy if exists "Own profile upsert" on public.profiles;
drop policy if exists "Own profile update" on public.profiles;

drop policy if exists "Own credits read"   on public.user_credits;
drop policy if exists "Own credits update" on public.user_credits;

drop policy if exists "Own ledger read"    on public.credit_ledger;
drop policy if exists "Own ledger insert"  on public.credit_ledger;

drop policy if exists "Own slideshows read"   on public.slideshows;
drop policy if exists "Own slideshows insert" on public.slideshows;

-- Profiles
create policy "Own profile read"
  on public.profiles for select
  using (auth.uid() = user_id);

create policy "Own profile upsert"
  on public.profiles for insert
  with check (auth.uid() = user_id);

create policy "Own profile update"
  on public.profiles for update
  using (auth.uid() = user_id)
  with check (auth.uid() = user_id);

-- Credits
create policy "Own credits read"
  on public.user_credits for select
  using (auth.uid() = user_id);

create policy "Own credits update"
  on public.user_credits for update
  using (auth.uid() = user_id)
  with check (auth.uid() = user_id);

-- Ledger (read-only to owner; inserts happen via RPC)
create policy "Own ledger read"
  on public.credit_ledger for select
  using (auth.uid() = user_id);

-- Slideshows
create policy "Own slideshows read"
  on public.slideshows for select
  using (auth.uid() = user_id);

create policy "Own slideshows insert"
  on public.slideshows for insert
  with check (auth.uid() = user_id);

-- ============= FUNCTIONS (RPC) =============
-- Atomic decrement (returns true/false). Logged in ledger.
create or replace function public.decrement_credits(p_user_id uuid, p_amount int)
returns boolean
language plpgsql
security definer
set search_path = public
as $$
declare current_credits int;
begin
  select credits into current_credits
  from public.user_credits
  where user_id = p_user_id
  for update;

  if current_credits is null then
    raise exception 'user_credits row missing for %', p_user_id;
  end if;

  if current_credits < p_amount then
    return false;
  end if;

  update public.user_credits
  set credits = credits - p_amount, updated_at = now()
  where user_id = p_user_id;

  insert into public.credit_ledger (user_id, delta, reason)
  values (p_user_id, -p_amount, 'slideshow.generate');

  return true;
end;
$$;

-- Increment helper (webhooks, refunds, admin tools)
create or replace function public.increment_credits(p_user_id uuid, p_amount int, p_reason text default 'topup', p_ref text default null)
returns void
language sql
security definer
set search_path = public
as $$
  update public.user_credits
  set credits = coalesce(credits,0) + p_amount, updated_at = now()
  where user_id = p_user_id;

  insert into public.credit_ledger (user_id, delta, reason, ref)
  values (p_user_id, p_amount, p_reason, p_ref);
$$;

-- ============= AUTH TRIGGER (on signup) =============
create or replace function public.handle_new_user()
returns trigger
language plpgsql
security definer
set search_path = public
as $$
begin
  insert into public.profiles (user_id, email)
  values (new.id, new.email)
  on conflict (user_id) do nothing;

  insert into public.user_credits (user_id, credits)
  values (new.id, 3) -- FREE_STARTING_CREDITS
  on conflict (user_id) do nothing;

  return new;
end;
$$;

drop trigger if exists on_auth_user_created on auth.users;
create trigger on_auth_user_created
  after insert on auth.users
  for each row
  execute function public.handle_new_user();

